// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

/* solhint-disable no-empty-blocks */

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";

/**
 * Token callback handler.
 *   Handles supported tokens' callbacks, allowing account receiving these tokens.
 */
contract TokenCallbackHandler is IERC777Recipient, IERC721Receiver, IERC1155Receiver {
    function tokensReceived(
        address,
        address,
        address,
        uint256,
        bytes calldata,
        bytes calldata
    ) external pure override {
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155BatchReceived.selector;
    }

    function supportsInterface(bytes4 interfaceId) external view virtual override returns (bool) {
        return
            interfaceId == type(IERC721Receiver).interfaceId ||
            interfaceId == type(IERC1155Receiver).interfaceId ||
            interfaceId == type(IERC165).interfaceId;
    }
}

contracts/Luna.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./callback/TokenCallbackHandler.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "./verifier/PasskeyVerifier.sol";
import "./verifier/RecoveryVerifier.sol";

import "./utils/Conversion.sol";

contract Luna is TokenCallbackHandler, Initializable {

    string public DOMAIN;

    bytes public passkeyInputs;

    bytes32 public recoveryHash;
    string public email;

    PasskeyUltraVerifier public immutable PASSKEY_VERIFIER;
    RecoveryUltraVerifier public immutable RECOVERY_VERIFIER;

    mapping(bytes32 => bool) public usedNonces;

    uint256 private nonce;

    constructor(
        address passkeyVerifier_,
        address recoveryVerifier_
    ) {
        PASSKEY_VERIFIER = PasskeyUltraVerifier(passkeyVerifier_);
        RECOVERY_VERIFIER = RecoveryUltraVerifier(recoveryVerifier_);
    }

    function getNonce() public view returns (uint256) {
        return nonce;
    }

    function _useNonce() internal returns (uint256) {
        unchecked {
            return nonce++;
        }
    }

    function initialize(string memory domain ,bytes memory _passkeyInputs, bytes32 _recoveryHash, string memory _email) external initializer {
        _initialize(domain, _passkeyInputs, _recoveryHash, _email);
    }

    function _initialize(string memory domain, bytes memory _passkeyInputs, bytes32 _recoveryHash, string memory _email) internal virtual {
        DOMAIN = domain;
        passkeyInputs = _passkeyInputs;
        recoveryHash = _recoveryHash;
        email = _email;
    }

    function getCredentialId() public view returns (string memory) {
        (, ,string memory credentialId) = Conversion.decodeEncodedInputs(passkeyInputs);
        return credentialId;
    }

    function getPublicKey() public view returns (bytes32, bytes32) {
        (bytes32 pubkeyx, bytes32 pubkeyy, ) = Conversion.decodeEncodedInputs(passkeyInputs);
        return (pubkeyx, pubkeyy);
    }

    function verifyPasskey(bytes calldata proof, bytes32 message) public view returns (bool) {
        (bytes32 pubkeyx, bytes32 pubkeyy, ) = Conversion.decodeEncodedInputs(passkeyInputs);

        bytes32[] memory inputs = Conversion.convertPasskeyInputs(pubkeyx, pubkeyy, message);

        return PASSKEY_VERIFIER.verify(proof, inputs);
    }

    function usePasskey(bytes calldata proof, bytes32 message) internal returns (bool) {
        require(!usedNonces[message], "Nonce already used");
        (bytes32 pubkeyx, bytes32 pubkeyy,) = Conversion.decodeEncodedInputs(passkeyInputs);

        bytes32[] memory inputs = Conversion.convertPasskeyInputs(pubkeyx, pubkeyy, message);

        usedNonces[message] = true;
        return PASSKEY_VERIFIER.verify(proof, inputs);
    }

    function verifyRecovery(bytes calldata proof) public view returns (bool) {
        bytes32 message = Conversion.hashMessage(Strings.toString(getNonce()));

        bytes32[] memory _inputs = Conversion.convertInputs(message, recoveryHash);

        return RECOVERY_VERIFIER.verify(proof, _inputs);
    }

    function useRecovery(bytes calldata proof) internal returns (bool) {
        bytes32 message = Conversion.hashMessage(Strings.toString(_useNonce()));

        bytes32[] memory _inputs = Conversion.convertInputs(message, recoveryHash);

        return RECOVERY_VERIFIER.verify(proof, _inputs);
    }

    function execute(bytes calldata proof, bytes32 message,  address dest, uint256 value, bytes calldata func) external payable returns (bool) {
        require(usePasskey(proof, message), "Invalid passkey");
        _execute(dest, value, func);
        return true;
    }

    function executeBatch(bytes calldata proof, bytes32 message,  address[] calldata dest, uint256[] calldata value, bytes[] calldata func) external payable returns (bool) {
        require(usePasskey(proof, message), "Invalid passkey");
        _executeBatch(dest, value, func);
        return true;
    }

    function executePasskeyRecovery(bytes calldata proof, bytes memory _passkeyInputs) external payable returns (bool) {
        require(useRecovery(proof), "Invalid recovery");
        _changePasskeyInputs(_passkeyInputs);
        return true;
    }

    function executeChangeRecovery(bytes calldata proof, bytes32 _recoveryHash, string memory _email) external payable returns (bool) {
        require(useRecovery(proof), "Invalid recovery");
        _changeRecovery(_recoveryHash, _email);
        return true;
    }

    function _changePasskeyInputs(bytes memory _passkeyInputs) internal  {
		passkeyInputs = _passkeyInputs;
	}

    function _changeRecovery(bytes32 _recoveryHash, string memory _email) internal  {
        recoveryHash = _recoveryHash;
        email = _email;
    }

    function _execute(address dest, uint256 value, bytes calldata func) internal {
        _call(dest, value, func);
    }

    function _executeBatch(address[] calldata dest, uint256[] calldata value, bytes[] calldata func) internal {
        require(dest.length == func.length && (value.length == 0 || value.length == func.length), "wrong array lengths");
        if (value.length == 0) {
            for (uint256 i = 0; i < dest.length; i++) {
                _call(dest[i], 0, func[i]);
            }
        } else {
            for (uint256 i = 0; i < dest.length; i++) {
                _call(dest[i], value[i], func[i]);
            }
        }
    }

	function _call(address target, uint256 value, bytes memory data) internal {
        (bool success, bytes memory result) = target.call{value : value}(data);
        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }
    }

    receive() external payable {}

    fallback() external payable {}
}

contracts/LunaFactory.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Luna.sol";
import "./LunaStorage.sol";

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";

contract LunaFactory is LunaStorage, ERC2771Context {
    Luna public immutable LUNA_IMPLEMENTATION;

    constructor(address _trustedForwarder, address _passkeyVerifier, address _recoveryVerifier) ERC2771Context(_trustedForwarder) {
        LUNA_IMPLEMENTATION = new Luna(_passkeyVerifier, _recoveryVerifier);
    }

    modifier onlyTrustedForwarder() {
        require(isTrustedForwarder(msg.sender), "LunaFactory: caller is not the trusted forwarder");
        _;
    }

    function createAccount(string memory name, bytes memory _passkeyInputs, bytes32 _recoveryHash, string memory _email, uint256 salt) external returns (Luna ret) { 
        if(LunaNameToDetails[name].isUsed) {
            ret = Luna(payable(LunaNameToDetails[name].walletAddress));
            return ret;
        }

        address addr = getAddress(name, _passkeyInputs, _recoveryHash, _email, salt);
        uint codeSize = addr.code.length;
        if (codeSize > 0) {
            return Luna(payable(addr));
        }
        ret = Luna(payable(new ERC1967Proxy{salt : bytes32(salt)}(
                address(LUNA_IMPLEMENTATION),
                abi.encodeCall(Luna.initialize, (name, _passkeyInputs, _recoveryHash, _email))
            )));
        addLuna(name, address(ret));
    }

    function getAddress(string memory name, bytes memory _passkeyInputs, bytes32 _recoveryHash, string memory _email, uint256 salt) public view returns (address) {
       return Create2.computeAddress(bytes32(salt), keccak256(abi.encodePacked(
            type(ERC1967Proxy).creationCode,
            abi.encode(
                address(LUNA_IMPLEMENTATION),
                abi.encodeCall(Luna.initialize, (name, _passkeyInputs, _recoveryHash, _email))
            )
        )));
    }

      function executeLunaTx(string memory name, bytes calldata proof, bytes32 message, address dest, uint256 value, bytes calldata func) onlyTrustedForwarder isValidLuna(name) external returns (bool) {
        Luna luna = Luna(payable(address(LunaNameToDetails[name].walletAddress)));

        return luna.execute(proof, message, dest, value, func);
    }

    function executeLunaBatchTx(string memory name, bytes calldata proof, bytes32 message, address[] calldata dests, uint256[] calldata values, bytes[] calldata funcs) onlyTrustedForwarder isValidLuna(name) external returns (bool) {
        Luna luna = Luna(payable(address(LunaNameToDetails[name].walletAddress)));

        return luna.executeBatch(proof, message, dests, values, funcs);
    }

    function executeLunaPasskeyRecovery(string memory name, bytes calldata proof, bytes memory _passkeyInput) onlyTrustedForwarder isValidLuna(name) external returns (bool) {
        Luna luna = Luna(payable(address(LunaNameToDetails[name].walletAddress)));

        return luna.executePasskeyRecovery(proof, _passkeyInput);
    }

    function executeLunaChangeRecovery(string memory name, bytes calldata proof, bytes32 _recoveryHash, string memory email) onlyTrustedForwarder isValidLuna(name) external returns (bool) {
        Luna luna = Luna(payable(address(LunaNameToDetails[name].walletAddress)));

        return luna.executeChangeRecovery(proof, _recoveryHash, email);
    }
}

contracts/LunaStorage.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract LunaStorage {

    struct LunaDetails {
        address walletAddress;
        bool isUsed;
    }

    mapping(string => LunaDetails) public LunaNameToDetails;

    function addLuna(string memory name, address walletAddress) internal {
        LunaNameToDetails[name] = LunaDetails(walletAddress, true);
    }

    function _checkLuna(string memory name) internal view {
        require(LunaNameToDetails[name].isUsed, "Luna: Invalid Luna");
    }

    function getLuna(string memory name) external view returns (LunaDetails memory) {
        LunaDetails memory details = LunaNameToDetails[name];
        return details;
    }

    modifier isValidLuna(string memory name) {
        _checkLuna(name);
        _;
    }

}

contracts/utils/Conversion.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/Strings.sol";

library Conversion {
    error InvalidAuthenticatorData();
    error InvalidClientData();

    function convertInputs(bytes32 message, bytes32 _hash) internal pure returns (bytes32 [] memory){
        bytes32[] memory byte32Inputs = new bytes32[](33);

        for (uint256 i = 0; i < 32; i++) {
            byte32Inputs[i] = convertToPaddedByte32(message[i]);
        }

        byte32Inputs[32] = _hash;

        return byte32Inputs;
    }

    function convertPasskeyInputs(bytes32 pubkeyx, bytes32 pubkeyy, bytes32 message) internal pure returns (bytes32 [] memory){
        bytes32[] memory byte32Inputs = new bytes32[](96);

        for (uint256 i = 0; i < 32; i++) {
            byte32Inputs[i] = convertToPaddedByte32(pubkeyx[i]);
            byte32Inputs[i + 32] = convertToPaddedByte32(pubkeyy[i]);
            byte32Inputs[i + 64] = convertToPaddedByte32(message[i]);
        }

        return byte32Inputs;
    }

    function convertToPaddedByte32(bytes32 value) internal pure returns (bytes32) {
        bytes32 paddedValue;
        paddedValue = bytes32(uint256(value) >> (31 * 8));
        return paddedValue;
    } 

    function hashMessage(string memory message) internal pure returns (bytes32) {
        string memory messagePrefix = "\x19Ethereum Signed Message:\n";

        string memory lengthString = Strings.toString(bytes(message).length);

        string memory concatenatedMessage = string(abi.encodePacked(messagePrefix, lengthString, message));

        return keccak256(bytes(concatenatedMessage));
    }

     function decodeEncodedInputs(
        bytes memory _inputs
    )
        internal
        pure
        returns (bytes32, bytes32, string memory)
    {
        (
            bytes32 pubkeyx,
            bytes32 pubkeyy,
            string memory credentialId
        ) = abi.decode(_inputs, (bytes32, bytes32, string));

        return (
            pubkeyx,
            pubkeyy,
            credentialId
        );
    }
}

